import {  useState } from "react";
import { Link,useNavigate } from 'react-router-dom';
import axios from "axios";
//import Validation from './LoginValidation';


function Login() {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const navigate = useNavigate();

    const email_pattern=/^[^\s@]+@[^\s@]+\.[^\s@]+$/
    const password_pattern=/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/

    const values={email,password};
    //const[errors,setErrors]=useState({})

    async function login(e) {
        e.preventDefault();
        //setErrors(Validation(values));
        try {
            await axios.post("http://localhost:8081/api/register/login", {
            email: email,
            password: password,
            }).then((res) =>
            {
                console.log(res.data);

                if(values.email ==="" && values.password==="" ){
                    alert("Fill all the inputs");
                }
                else if(values.email !== res.data.email && values.password !== res.data.password ){
                   alert ("Email or Password not match");
                }
                else{
                    navigate('/Employees');
                }
            }, fail => {
                console.error(fail);
            });
            }catch (err) {
            alert(err);
          }
    }
  
    return (
        <div>
        <div className="container">
            <div className="d-flex justify-context-center align-items-center">
            <div className="card col-md-4 offset-md-3 offset-md-3 w-40 boarder loginform">
            <div className="card-body">
                <h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Login</h2>
                    <form >
                        <div className="form-group mb-2">
                            <label classname="form-label">Email</label>
                            <input 
                                type="email" 
                                className="form-control" 
                                placeholder="Enter Email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}>
                            </input>
                            {/*{errors.email && <span className="text-danger">{errors.email}</span>}*/}
                        </div>
                        <div className="form-group mb-2">
                            <label classname="form-label">Password</label>
                            <input 
                                type="password" 
                                className="form-control"
                                placeholder="Enter Password"
                                value={password}
                                onChange={(e)=>setPassword(e.target.value)}>
                            </input>
                            {/*{errors.password && <span className="text-danger">{errors.password}</span>}*/}
                        </div>
                        <br></br>
                        <div className='d-grid'>
                        <button type="submit" className="btn btn-primary" onClick={login}  style={{backgroundColor:"coral"}}>Login</button>
                        </div>
                        <br></br>
                        <div className="text-center">
                             Don't have an Account?
                            &nbsp;
                            <Link to='/Register' style={{color:"purple"}}> Sign Up </Link>
                        </div>
                    </form>
 
                </div>
            </div>
        </div>
        </div>
     </div>
    )
  }
  
  export default Login;
