import React, {Fragment,useState, useEffect} from 'react';
import {Button, Table} from 'react-bootstrap';
import axios from 'axios';
import './EmployeeStyle.css';
import {toast} from "react-toastify";
import {Link} from 'react-router-dom'


function Employees(){

    const [employees, setEmployees] = useState([]);


    useEffect(() => {
            axios.get('http://localhost:8081/api/getEmployee')     
            .then(res=>setEmployees(res.data))
            .catch(err=>console.log(err));
        }, []);

    const deleteEmployee = (id) => {
             if(window.confirm("Are you sure that you want to delete that employee"))
                axios.delete(`http://localhost:8081/api/remove/${id}`);
                toast.success("Employee Deleted successfully"); 
    };

    return(
        <Fragment>
            <div className="container">
            <div className="text-center" style={{color:"black",fontStyle:"normal",textAlign:"center"}}><h3>List of Employee</h3> </div>
                <Table striped bordered hover size="sm" className="center">
                    <thead>
                        <tr style={{color:"black"}}>
                                <th>Id</th>
                                <th>Name</th>
                                <th>Gender</th>
                                <th>DateOfBirth</th>
                                <th>Salary </th>
                                <th>Department</th>
                                <th></th>
                        </tr>
                    </thead>
                    <tbody >
                            {employees.map((employee,index) => {
                                return <tr key={employee.id} >
                                       <th scope="row">{index+1}</th>
                                        <td>{employee.empName}</td>
                                        <td>{employee.sex}</td>
                                        <td>{employee.dob}</td>
                                        <td>{employee.salary}</td>
                                        <td>{employee.department}</td> 
                                                         
                                        <td>
                                            <Link  to={`/edit-employee/${employee.id}`} >
                                                <Button style={{backgroundColor:"violet"}}>Edit</Button>
                                            </Link>
                                            &nbsp;
                                            <Button className="btn btn-danger" onClick={() => deleteEmployee(employee.id)}
                                            style={{marginLeft:"10px"}}>Delete</Button>
                                        </td>
                                    </tr>
                            })}
                        
                    </tbody>
                    <br>
                    </br>
                     <div>
                    <Link  to="/add-employee">
                        <Button clasName="btn btn-success" size="lg" style={{backgroundColor:"coral"}}>Add Employee</Button>
                    </Link>
                    </div>
                    <br></br>
                </Table>
                </div>
        </Fragment>
    )

}

export default Employees;
