import React, {useState, useEffect} from 'react'
import {Link,useNavigate, useParams} from 'react-router-dom'
import axios from "axios";
import {toast} from "react-toastify";

const initialState={
    empName:"",
    sex:"",
    dob:"",
    salary:"",
    department:"",
};

const AddEdit = () =>{
    const [state,setState] = useState(initialState);
    
    
    const navigate = useNavigate();
    const {id}=useParams();

    const {empName,sex, dob, salary,department}=state;

    const handleSubmit=(e)=>{

        e.preventDefault();
        if(!empName || !sex || !dob || !salary || !department){
            toast.error("Please provide value into each input field");
        }else{
            if(!id){
                axios.post("http://localhost:8081/api/post",{
                empName, 
                sex, 
                dob, 
                salary, 
                department
             }) 
             .then(()=>{
                setState({empName:"", sex:"", dob:"", salary:"", department:""});
             }) 
             .catch((err)=>toast.error(err.response.data));
             toast.success("Employee Added Successfully");
            }else{
                axios.put(`http://localhost:8081/api/editEmployee/${id}`,{
                empName, 
                sex, 
                dob, 
                salary, 
                department,
             }) 
             .then(()=>{
                setState({empName:"", sex:"", dob:"", salary:"", department:""});
             }) 
             .catch((err)=>toast.error(err.response.data));
             toast.success("Employee Edited Successfully");
            }
             
             setTimeout(() =>navigate.push("/Employees"),500);
            }  
        };

    const handleInputChange=(e)=>{
        const{empName,value}=e.target;
        setState({...state,[empName]:value});
        /*setState({...state,[sex]:value});
        setState({...state,[dob]:value});
        setState({...state,[salary]:value});
        setState({...state,[department]:value});*/
    };

    useEffect(()=>{
        axios.get(`http://localhost:8081/api/getEmployee/${id}`)
        .then((res)=>setState({...res.data[0]}));
    },[id]);

    const title=() =>{
        if(id){
            return <h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Edit Employee</h2>
        }else{
            return <h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Add Employee</h2>
        }
    }
    return(
        <div>
            <div classname="container">
                <div className="d-flex justify-context-center align-items-center">
                    <div className="card col-md-6 offset-md-3 offset-md-3 w-50 boarder addemployeeform">
                        <div className="card-body">
                        {
                            title()
                        }
                            <form onSubmit={handleSubmit}>
                                <div className="form-group mb-2">
                                    <label classname="form-label">Name</label>
                                    <input
                                        type="text"
                                        placeHolder="Enter Employee Name"
                                        name="empName"
                                        id="empName"
                                        className="form-control"
                                        value={empName || ""}
                                        onChange={handleInputChange}>
                                    </input>
                                </div>
                                <div className="form-group mb-2">
                                    <label classname="form-label">Gender</label>
                                    <br></br>
                                    <input
                                        type="radio"
                                        name="sex"
                                        value="Male"
                                        onChange={handleInputChange}/>Male
                                    &nbsp;
                                    <input
                                        type="radio"
                                        name="sex"
                                        value="Female"
                                        onChange={handleInputChange}>
                                    </input>Female
                                </div>
                                <div className="form-group mb-2">
                                    <label classname="form-label">DOB</label>
                                    <input
                                        type="date"
                                        name="dob"
                                        className="form-control"
                                        value={dob || ""}
                                        onChange={handleInputChange}>
                                    </input>
                                </div>
                                <div className="form-group mb-2">
                                    <label classname="form-label">Salary</label>
                                    <input
                                        type="text"
                                        placeHolder="Enter Salary"
                                        name="salary"
                                        className="form-control"
                                        value={salary || ""}
                                        onChange={handleInputChange}>
                                    </input>
                                </div>
                                <div className="form-group mb-2">
                                    <label classname="form-label">Department</label>
                                    <input
                                        type="text"
                                        placeHolder="Enter Department"
                                        name="department"
                                        className="form-control"
                                        value={department || ""}
                                        onChange={handleInputChange}>
                                    </input>
                                </div>
                                <div>
                                    <input type="submit" value={id ? "Edit" : "Save"}/>
                                    &nbsp;
                                    <Link  to="/Employees">
                                        <input type="button" value="Cancel" />
                                        </Link>
                                </div>
                            </form>
                            <br></br>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    )
}

export default AddEdit;
