import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {toast} from "react-toastify";
import {Link} from 'react-router-dom'


function Employees(){

    const [employees, setEmployees] = useState([]);

    const loadData=async()=>{
        const response=await axios.get("http://localhost:8081/api/getEmployee");
        setEmployees(response.data);
    };

    useEffect(() => {
            loadData();        
        }, []);

    const deleteEmployee = (id) => {
        if(window.confirm("Are you sure that you want to delete that employee")){}
         axios.delete(`http://localhost:8081/api/remove/${id}`);
         toast.success("Employee Deleted successfully");
         setTimeout(()=> loadData(),500);     
    };

    return(
            <div className='d-flex vh-100 bg-primary justify-content-cneter align-items-center'>
            <div className='w-50 bg white rounded' style={{color:"black",fontStyle:"normal"}}><h3>List of Employee</h3> </div>
                <table classname='table'>
                    <thead>
                        <tr style={{color:"black", textalign:"center"}}>
                                <th>Id</th>
                                <th>Name</th>
                                <th>Gender</th>
                                <th>DateOfBirth</th>
                                <th>Salary </th>
                                <th>Department</th>
                                <th></th>
                        </tr>
                    </thead>
                    <tbody >
                            {employees.map((employee,index) => {
                                return <tr key={employee.id} >
                                       <th scope="row">{index+1}</th>
                                        <td>{employee.empName}</td>
                                        <td>{employee.sex}</td>
                                        <td>{employee.dob}</td>
                                        <td>{employee.salary}</td>
                                        <td>{employee.department}</td> 
                                                         
                                        <td>
                                            <Link  to={`/editEmployee/${employee.id}`} >
                                                <button style={{backgroundColor:"violet"}}>Edit</button>
                                            </Link>
                                            &nbsp;
                                            <button className="btn btn-danger" onClick={() => deleteEmployee(employee.id)}
                                            style={{marginLeft:"10px"}}>Delete</button>
                                        </td>
                                    </tr>
                            })}
                        
                    </tbody>
                </table>
                <br>
                </br>
                <div>
                <Link clasName="btn btn-primary mb-2"  to="/addEmployee">
                    <button size="lg" style={{backgroundColor:"coral"}}>Add Employee</button>
                </Link>
                </div>
                <br></br>
            </div>
    )

}

export default Employees;
