import React, {useState} from 'react'
import {Link,useNavigate} from 'react-router-dom'
import axios from "axios";
import './LoginStyle.css';


const AddEmployee = () =>{
    
    const [empName, setEmpName]=useState('')
    const [sex, setSex]=useState('')
    const [dob, setDob]=useState('')
    const [salary, setSalary]=useState('')
    const [department, setDepartment]=useState('')
    
    const navigate = useNavigate();
    
    const {id}=useParams();
    const employee={empName,sex,dob,salary,department};

    const saveOrEditEmployee=(e)=>{
        e.preventDefault();
        if(employee.empName !== "" && employee.sex !== "" && employee.dob !== "" && employee.salary !== "" && employee.department !== "" ){
            if(!id){
            axios.post("http://localhost:8081/api/post", {empName, sex, dob, salary, department})
            .then((res) => 
            {
                console.log(res.data);
                navigate("/Employees");
            }).catch(err=> console.log(err));
        }else{
            axios.put(`http://localhost:8081/api/editEmployee/${id}`, {empName, sex, dob, salary, department})
                .then((res)=>
                {
                    console.log(res.data);
                    navigate("/Employees")
                }).catch((error) =>{
                        console.log(error);
                });
        }
            }else{
                alert("Please fill all the inputs");
            }
    }
    useEffect (()=>{

        axios.get(`http://localhost:8081/api/getEmployee/${id}`).then((response)=>{
            setEmpName(response.data.empName);
            setSex(response.data.sex);
            setDob(response.data.dob);
            setSalary(response.data.salary);
            setDepartment(response.data.department);
        }).catch(error =>{
            console.log(error)
        });
    },[id]);
    
    const title=() =>{
        if(id){
            return <h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Edit Employee</h2>
        }else{
            return <h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Add Employee</h2>
        }
    }

    return(
        <div>
        <div classname="container">
            <div className="d-flex justify-context-center align-items-center">
                <div className="card col-md-6 offset-md-3 offset-md-3 w-50 boarder addemployeeform">
                    <div className="card-body">
                    {/*<h2 className="text-center" style={{fontStyle:"System,900,bold"}}>Add Employee</h2>*/}
                        <form>
                            <div className="form-group mb-2">
                                <label classname="form-label">Name</label>
                                <input
                                    type="text"
                                    placeHolder="Enter Employee Name"
                                    name="empName"
                                    className="form-control"
                                    value={empName}
                                    onChange={(e)=>setEmpName(e.target.value)}>
                                </input>
                            </div>
                            <div className="form-group mb-2">
                                <label classname="form-label">Gender</label>
                                <br></br>
                                <input
                                    type="radio"
                                    name="sex"
                                    value="Male"
                                    onChange={(e)=>setSex(e.target.value)}/>Male
                                &nbsp;
                                <input
                                    type="radio"
                                    name="sex"
                                    value="Female"
                                    onChange={(e)=>setSex(e.target.value)}></input>Female
                                
                            </div>
                            <div className="form-group mb-2">
                                <label classname="form-label">DOB</label>
                                <input
                                    type="date"
                                    name="dob"
                                    className="form-control"
                                    value={dob}
                                    onChange={(e)=>setDob(e.target.value)}>
                                </input>
                            </div>
                            <div className="form-group mb-2">
                                <label classname="form-label">Salary</label>
                                <input
                                    type="text"
                                    placeHolder="Enter Salary"
                                    name="salary"
                                    className="form-control"
                                    value={salary}
                                    onChange={(e)=>setSalary(e.target.value)}>
                                </input>
                            </div>
                            <div className="form-group mb-2">
                                <label classname="form-label">Department</label>
                                <input
                                    type="text"
                                    placeHolder="Enter Department"
                                    name="department"
                                    className="form-control"
                                    value={department}
                                    onChange={(e)=>setDepartment(e.target.value)}>
                                </input>
                            </div>
                            {/*<div className="form-group mb-2">
                                <label classname="form-label">Photo</label>
                                <input
                                    type="file"
                                    name='file'
                                    accept="image/*"
                                    className="form-control"
                                    onChange={(e)=>setPhoto(e.target.files[0])}>
                                </input>
                            </div>*/}
                            
                            <div>
                                <button className="btn btn-success" onClick={(e)=> saveOrEditEmployee(e)}>Submit</button>
                                &nbsp;
                                <Link  to="/Employees" className="btn btn-danger"><button>Cancel</button></Link>
                            </div>
                        </form>
                        <br></br>
                    </div>
                </div>
            </div>
        </div>

    </div>
)
}
export default AddEmployee;
